open Core
open Snarky
open Marlin_plonk_bindings

module Public_input (Intf : Snark_intf.Run with type prover_state = unit and type field = Pasta_fp.t) = struct
  open Intf
  open Typ
  module Ecc = Plonk.Ecc.Constraints (Intf)
  
  let rec double p n = if n < 1 then p else double (Ecc.Basic.double1 p) Int.(n - 1)

  (* SIGNATURE SCHEME EC BASE POINT *)
  let p = Ecc.Basic.random ()
  let pn = double p Field.size_in_bits
  (* NOTARY SECRET KEY *)
  let a = Int.(Random.int max_value)
  (* NOTARY PUBLIC KEY *)
  let q = Ecc.Basic.mul p a

  let public_input = Field.Constant.(p, pn, q)

end

